mapper.update:
update sky_take_out.orders

    status = #{status},
    pay_status = #{payStatus},
    checkout_time = #{checkoutTime}
    cancel_time = #{cancelTime}
    cancel_reason = #{cancelReason}

where id = #{id}

mapper.select:
select * from sky_take_out.orders

    status = #{status} and
    user_id = #{userId}
