mapper.select:
select number from shopping_cart

    dish_id = #{dishId}
    setmeal_id = #{setmealId}
    and user_id = #{userId}

mapper.update:
update shopping_cart set number = #{number}

    dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and dish_flavor = #{dishFlavor}
    and user_id = #{userId}

mapper.delete:
delete from shopping_cart

    dish_id = #{dishId}
    setmeal_id = #{setmealId}
    and user_id = #{userId}
